<?php 
/**
 * Block Map section
 */

$block_ID = $block['id'];

$title = get_field( 'section_bwm_title' );
$content = get_field( 'section_bwm_content' );
$places = get_field( 'section_bwm_markers' );
$google_maps_api_key = get_field( 'google_maps_api_key', 'option' );

$mapJson = json_encode([
    'places' => $places,
    'lat' => get_field('section_bwm_lat'),
    'lng' => get_field('section_bwm_lng'),
    'zoom' => get_field('section_bwm_zoom'),
]);
?>

<section class="block-map" id="<?= $block_ID; ?>">
    <div class="container">
        <?php if ($title): ?>
            <h2 class="heading-underline heading-dot fade-in"><?= $title; ?></h3>
        <?php endif; ?>
        <?php if ($content): ?>
            <div class="fade-in block-map__content">
                <?= $content; ?>
            </div>
        <?php endif; ?>
        <div class="block-map__wrapper fade-in">
            <?php if ($places): ?>
                <div class="block-map__places">
                    <?php foreach ($places as $place): ?>
                        <div class="block-map__place">
                            <strong><?= esc_html($place['name']); ?></strong>
                            <p><?= esc_html($place['address'] ?? ''); ?></p>
                        </div>
                    <?php endforeach; ?>
                </div>
            <?php endif; ?>
            <div class="block-map__map">
                <div
                    id="map-<?= $block_ID ?>"
                    class="map"
                    data-map='<?= $mapJson ?>'
                ></div>
            </div>
        </div>
    </div>
</section>

<script>
function initMaps() {
    const mapElements = document.querySelectorAll('.map');
    let currentInfoWindow = null;

    // Iterate through each map
    mapElements.forEach(mapElement => {
        const mapData = JSON.parse(mapElement.getAttribute('data-map'));

        // Set map options
        const mapOptions = {
            center: { lat: parseFloat(mapData.lat), lng: parseFloat(mapData.lng) },
            zoom: parseInt(mapData.zoom),
        };

        // Initialize the map
        try {
            const map = new google.maps.Map(mapElement, mapOptions);

            // Add markers
            mapData.places.forEach((place, index) => {
                const marker = new google.maps.Marker({
                    position: { 
                        lat: parseFloat(place.latitude), 
                        lng: parseFloat(place.longitude) 
                    },
                    map: map,
                    title: place.name,
                    icon: {
                        url: 'data:image/png;base64,',
                        scaledSize: new google.maps.Size(32, 32),
                    }
                });

                // Information for the marker
                const infoWindow = new google.maps.InfoWindow({
                    content: `
                        <div class="block-map__info">
                            <div class="block-map__info-title">${place.name}</div>
                            <div>${place.address || ''}</div>
                        </div>
                    `
                });

                // Add listener for the marker
                marker.addListener('click', () => {
                    if (currentInfoWindow) {
                        currentInfoWindow.close();
                    }
                    infoWindow.open(map, marker);
                    currentInfoWindow = infoWindow;
                });

                // Handle double click for zoom
                marker.addListener('dblclick', () => {
                    let currentZoom = map.getZoom();
                    map.setZoom(currentZoom + 1);
                    map.setCenter(marker.getPosition());
                });

                // Add click event handling for list item
                const placeItem = document.querySelectorAll('.block-map__place')[index];
                placeItem.addEventListener('click', () => {
                    google.maps.event.trigger(marker, 'click');
                });
            });
        } catch (error) {
            console.error('Error initializing map:', error);
        }
    });
}
</script>

<script async defer src="https://maps.googleapis.com/maps/api/js?key=<?= $google_maps_api_key ?>&callback=initMaps"></script>